package com.igsl.migration.service;

import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;
import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;
import com.igsl.migration.mapper.ApplicantRecordMapper;
import com.igsl.migration.model.ApplicantRecord;
import org.apache.logging.log4j.LogManager;
import org.apache.logging.log4j.Logger;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Service;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.util.Base64;
import java.util.List;
import java.util.Map;

@Service
public class ApplicantRecordService extends ServiceImpl<ApplicantRecordMapper, ApplicantRecord>  {

    private static final Logger logger = LogManager.getLogger(ApplicantRecordService.class);

    @Value("${photoPath}")
    private String photoPath;

    private static final String defaultPhotoTag = "<img style='width: 100%; height: 100%; object-fit: contain; display: block; margin: 0; background: transparent;' alt='card holder's photo' src='data:image/png;base64,'>";

    /**
     * Get base64 encoded image tag for applicant photo
     * @param applicantId The ID of the applicant
     * @return HTML img tag containing base64 encoded image, or empty string if photo not found
     */
    public String getPhotoBase64ImgTag(Integer applicantId) throws IOException {

        QueryWrapper<Map<String, Object>> queryWrapper = new QueryWrapper<>();
        queryWrapper.eq("A.applicant_id", applicantId)
                .last("LIMIT 1");  // 限制返回一条记录
        Map<String, Object> photoInfo = this.baseMapper.getApplicantInfoByApplicantId(queryWrapper);
        if (photoInfo == null || photoInfo.get("image_path") == null) {
            return defaultPhotoTag; // Return default photo if no photo found
        }

        // Construct full file path
        String imagePath = photoInfo.get("image_path").toString();
        String fullPath = photoPath + imagePath;

        // Check if file exists
        File photoFile = new File(fullPath);
        if (!photoFile.exists()) {
            return defaultPhotoTag;
        }

        try {
            // Read file and convert to base64
            byte[] photoData = Files.readAllBytes(photoFile.toPath());
            String base64Image = Base64.getEncoder().encodeToString(photoData);

            // Get image MIME type
            String mimeType = Files.probeContentType(photoFile.toPath());
            if (mimeType == null) {
                mimeType = "image/png"; // Default MIME type
            }

            // Build img tag with fixed width of 135px
            return String.format("<img style='width: 100%%; height: 100%%; object-fit: contain; display: block; margin: 0; background: transparent;' alt='card holder's photo' src='data:%s;base64,%s'>",
                    mimeType, base64Image);

        } catch (IOException e) {
            return defaultPhotoTag;
        }
    }

    public List<ApplicantRecord> getApplicantsWithVariantChars(int limit, int offset) {
        return this.baseMapper.getApplicantsWithVariantChars(limit, offset);
    }
}

